/* eslint-disable max-len */
/* eslint-disable jsx-a11y/label-has-associated-control */
// import React from 'react';
// import { Button } from 'antd';

import { React, useState } from 'react';
import { SwitchMultiButton } from 'switch-multi-button';
import { Button } from 'antd';
import './NewOfferForm.css';

const HoursInfo = ({ handleChange, values }) => (
  <div>
    <h3>Please fill out how many hours a week and how many weeks a year you work and then press done.</h3>
    <div className='input-box'>
      <label>
        <input
          className='small-input'
          type='number'
          placeholder='Hours per week'
          value={values.hours}
          onChange={handleChange('hours')}
        />
      </label>
      <label>
        <input
          className='small-input'
          type='number'
          placeholder='Weeks per year'
          value={values.week}
          onChange={handleChange('weeks')}
        />
      </label>
    </div>
    <div className='flex-container'>
      <Button type='button' className='yes-no-button' onClick={handleChange('total')}>Done</Button>
    </div>
  </div>
);

const PaymentRateSwitch = ({ handleChange, values }) => {
  const [state, setState] = useState('year');

  if (state === 'year') {
    return (
      <SwitchMultiButton
        className='wage-toggle'
        value={state}
        setValue={setState}
        style={{ fontSize: 18, fontWeight: 20 }}
        buttons={[
          {
            text: 'Year',
            value: 'year',
          },
          {
            text: 'Hour',
            value: 'hour',
          },
        ]}
      />
    );
  }
  return (
    <div>

      <div className='input-box'>
        <SwitchMultiButton
          className='wage-toggle'
          value={state}
          setValue={setState}
          style={{ fontSize: 18, fontWeight: 20 }}
          buttons={[
            {
              text: 'Year',
              value: 'year',
            },
            {
              text: 'Hour',
              value: 'hour',
            },
          ]}
        />
      </div>

      <div>
        <HoursInfo
          handleChange={handleChange}
          values={values}
        />
      </div>

    </div>
  );
};

const CompanyDetails = ({ handleChange, values }) => (
  <div>
    <label>
      <input
        type='text'
        placeholder={
          (values.recruiterCompany)
            ? 'Client Name'
            : 'Company Name'
        }
        value={values.company}
        onChange={handleChange('company')}
      />
      <select type='text' placeholder='*State' value={values.state} onChange={handleChange('state')}>
        <option value='' disabled selected>*State</option>
        <option value='Alabama'>Alabama</option>
        <option value='Alaska'>Alaska</option>
        <option value='Arizona'>Arizona</option>
        <option value='Arkansas'>Arkansas</option>
        <option value='California'>California</option>
        <option value='Colorado'>Colorado</option>
        <option value='Connecticut'>Connecticut</option>
        <option value='Delaware'>Delaware</option>
        <option value='Florida'>Florida</option>
        <option value='Georgia'>Georgia</option>
        <option value='Hawaii'>Hawaii</option>
        <option value='Idaho'>Idaho</option>
        <option value='Illinois'>Illinois</option>
        <option value='Indiana'>Indiana</option>
        <option value='Iowa'>Iowa</option>
        <option value='Kansas'>Kansas</option>
        <option value='Kentucky'>Kentucky</option>
        <option value='Louisiana'>Louisiana</option>
        <option value='Maine'>Maine</option>
        <option value='Maryland'>Maryland</option>
        <option value='Massachusetts'>Massachusetts</option>
        <option value='Michigan'>Michigan</option>
        <option value='Minnesota'>Minnesota</option>
        <option value='Mississippi'>Mississippi</option>
        <option value='Missouri'>Missouri</option>
        <option value='Montana'>Montana</option>
        <option value='Nebraska'>Nebraska</option>
        <option value='Nevada'>Nevada</option>
        <option value='New Hampshire'>New Hampshire</option>
        <option value='New Jersey'>New Jersey</option>
        <option value='New Mexico'>New Mexico</option>
        <option value='New York'>New York</option>
        <option value='North Carolina'>North Carolina</option>
        <option value='North Dakota'>North Dakota</option>
        <option value='Ohio'>Ohio</option>
        <option value='Oklahoma'>Oklahoma</option>
        <option value='Oregon'>Oregon</option>
        <option value='Pennsylvania'>Pennsylvania</option>
        <option value='Rhode Island'>Rhode Island</option>
        <option value='South Carolina'>South Carolina</option>
        <option value='South Dakota'>South Dakota</option>
        <option value='Tennessee'>Tennessee</option>
        <option value='Texas'>Texas</option>
        <option value='Utah'>Utah</option>
        <option value='Vermont'>Vermont</option>
        <option value='Virginia'>Virginia</option>
        <option value='Washington'>Washington</option>
        <option value='West Virginia'>West Virginia</option>
        <option value='Wisconsin'>Wisconsin</option>
        <option value='Wyoming'>Wyoming</option>
      </select>
      <h3>BASE SALARY</h3>
      <hr width='10%' align='left' />
      <div className='input-box'>
        <input
          className='small-input'
          type='number'
          placeholder='*Base Salary'
          value={values.base}
          onChange={handleChange('base')}
        />
      </div>
      <div className='input-box'>
        <PaymentRateSwitch
          handleChange={handleChange}
          values={values}
          className='salary-info'
        />
      </div>
    </label>
  </div>
);
export default CompanyDetails;
