import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';
import Login from './Login';
import Registration from './Registration';
import Home from './Home';
import NewOfferForm from './NewOfferForm';
import LoadGraphs from './examples/LoadGraphs';
import { myOffers } from './OfferAPI';

import Logout from './Logout';

class Routes extends Component {
  constructor(props) {
    super(props);
    const { href } = window.location;
    const lastToken = href.substring(href.lastIndexOf('/') + 1);
    this.state = { 
      defaultSelectedKey: lastToken,
      offers: []
     };
  }

  componentDidMount() {
    const retrieved = myOffers()
    .then(response => {
      this.setState({
        defaultSelectedKey: this.state.defaultSelectedKey,
        offers: response
      })
    });
    return retrieved
  };

  display() {
    const { loggedIn } = this.props;

    if (loggedIn) {
      {this.state.offers.map(offer => (
        console.log(offer.company)
      ))}
      return (
        <Switch>
          <Route path="/loadGraph" component={LoadGraphs} />
          <Route path="/Snap" component={Home} />
          <Route path="/new-offer" component={NewOfferForm} />
          <Route path="/logout" component={Logout} />
          <Route path="/" component={Home} />
          {this.state.offers.map(offer => (
              <Route path={"/" + offer.company} key={offer.company} component={LoadGraphs}/>
            ))}
        </Switch>
      );
    }
    return (
      <Switch>
        <Route path="/login" component={Login} />
        <Route path="/register" component={Registration} />
        <Route path="/" component={Home} />
      </Switch>
    );
  }

  render() {
    const { defaultSelectedKey, offers } = this.state;
    return (
      <div>
        {this.display()}
      </div>
    );
  }
}

export default Routes;
