        const values = {
          companyName, baseSalary, stocks, cashBonus, matchPercentage,
        };

        switch (step) {
          case 1:
            return (
              <div>
                <CompanyDetails
                  handleChange={this.handleChange}
                  values={values}
                />
                <button type="button" onClick={this.nextStep}>Next</button>
              </div>
            );
          case 2:
